import{_ as a}from"./plugin-vue_export-helper-c27b6911.js";import{r as i,o as g,c as u,d as c,a as t,e as l,w as e,b as o,f as d}from"./app-2685e7e5.js";const h={},_=t("p",null,"本文档是关于 Project Ultraman 的项目概述。本文档存在的目的，是为了让读者在阅读过本文档后，对项目成品有一个大致的印象。因此本文档将着重介绍玩法层面的相关内容。",-1),p=t("blockquote",null,[t("p",null,[t("strong",null,"我要成为他们的勇气！"),o("——雷斯帕")])],-1),f={class:"hint-container details"},m=t("summary",null,"快速链接",-1),x=t("p",null,"如果你已经读过这篇概览，或者想直接了解感兴趣的内容，请点击以下链接进行跳转",-1),b=t("strong",null,"故事剧情",-1),k=t("strong",null,"玩法设计",-1),v=t("strong",null,"程序架构",-1),B=t("h2",{id:"这是一个什么游戏",tabindex:"-1"},[t("a",{class:"header-anchor",href:"#这是一个什么游戏","aria-hidden":"true"},"#"),o(" 这是一个什么游戏？")],-1),j=t("p",null,"Project Ultraman 是一款以剧情为导向的开放世界动作冒险游戏。",-1),N=t("p",null,"在游戏中，玩家将扮演奥特曼的人间体雷斯帕，与亚波人的势力对抗，探索广袤的大地上，经历各式各样的冒险，甚至变身为 50m 高的奥特曼与超兽作战。",-1),V=t("li",null,[t("strong",null,"游戏名称"),o("：未定")],-1),q=t("li",null,[t("strong",null,"游戏类型"),o("：开放世界、动作、冒险、解谜")],-1),C=t("li",null,[t("strong",null,"目标群体"),o("："),t("code",null,"ESRB: E"),o(" 所有人")],-1),E=t("strong",null,"卖点",-1),R=t("strong",null,"成为那个给予人们勇气的英雄吧！",-1),w=d('<h2 id="故事梗概" tabindex="-1"><a class="header-anchor" href="#故事梗概" aria-hidden="true">#</a> 故事梗概</h2><p><strong>地球突然消失了</strong>。宇宙警备队立刻着手调查此事。随着调查的进行，宇宙警备队还发现太阳系中频繁出现强大的<strong>超兽</strong>。因此越来越多的奥特曼参与到这个棘手的事件中。</p><p>刚进入<strong>银十字军</strong>实习的<strong>雷斯帕</strong>在前往火星支援负伤的奥特曼时，遭遇了<strong>4D超兽：哈尔弥特</strong>，在与之交手的过程中，哈尔弥特吸收光线，产生了次元爆破，雷斯帕躲闪不及，被一同吸进了<strong>异次元</strong>之中...</p><p>等到雷斯帕醒来，他发现自己正身处消失的地球之上，更重要的是，他发现自己已经退回了人间体的形态，并且<strong>无法变身</strong>。</p><p>原来地球早已被<strong>亚波人</strong>占领，并拖进了异次元之中。这里的一切早已充斥着绝望的气息。得到神秘的<strong>怪兽少女</strong>帮助的雷斯帕经历了一场惊心动魄的逃亡，结果眼前竟出现了前所未见的强大超兽。而出乎意料的是，升华器此时却开始发出光芒...</p><p>等待他和地球的是希望，还是更深的绝望...？</p><h2 id="开放世界" tabindex="-1"><a class="header-anchor" href="#开放世界" aria-hidden="true">#</a> 开放世界</h2><p>开放世界是游戏主要的活动场景，玩家可以自由探索，可进行的活动包括但不限于</p><ul><li>探索开放世界，开拓地图</li><li>和扫荡的亚波人小队<strong>战斗</strong></li><li>探索<strong>光宇遗迹</strong>，解锁、应用新的能力</li><li>完成<strong>支线任务</strong>，完善对世界观的了解</li></ul><h2 id="怪兽战场模式" tabindex="-1"><a class="header-anchor" href="#怪兽战场模式" aria-hidden="true">#</a> 怪兽战场模式</h2><ul><li>怪兽战场模式是本游戏的核心玩法</li><li>怪兽战场只会在主线故事<strong>最后阶段</strong>出现，出现时，将会在开放世界地图中限制活动区域 <ul><li>战场的各处都会有 <ul><li>游击兵与亚波人的战斗</li><li>游击兵团与亚波人的据点</li><li>活动中的超兽</li></ul></li></ul></li><li>怪兽战场模式包含<strong>主要目标</strong>、<strong>次要目标</strong>和<strong>失败条件</strong><ul><li>完成<strong>全部</strong>的<strong>主要目标</strong>，则会胜利，并<strong>立即</strong>结束怪兽战场模式</li><li>结束前完成的<strong>次要目标</strong>，会在结束后获得对应<strong>奖励</strong></li><li>达成<strong>任意</strong>的<strong>失败条件</strong>，则会失败，并从最近的合格节点继续游戏</li></ul></li><li><strong>灾害</strong>：超兽的活动会造成相当程度的灾害 <ul><li>可能的影响：对应地区的物价，战斗后的评价</li></ul></li><li>玩家可以以<strong>人间体</strong>或<strong>奥特曼</strong>的形态活动，可自行规划作战方式 <ul><li><strong>人间体</strong><ul><li>可灵活地穿梭在战场各处，</li><li><strong>确保任务目标</strong>：支援游击兵，保护特定任务等</li><li><strong>为奥特曼作战提供便利</strong>：突袭亚波人据点，斩首亚波人头目，破坏亚波人的支援设施，破坏怪兽身体部位</li><li><strong>缩小灾害</strong>：吸引超兽至人烟稀少的地区</li><li>暴露可能：此时变身是否是不安全的 <ul><li>存在暴露可能：不可变身，基础战斗模式</li><li>不存在暴露可能 <ul><li>超兽存在：可变身<strong>巨大化</strong>，可开启<strong>耀斑模式</strong><sup class="footnote-ref"><a href="#footnote1">[1]</a><a class="footnote-anchor" id="footnote-ref1"></a></sup></li><li>超兽不存在：可开启<strong>耀斑模式</strong></li></ul></li></ul></li></ul></li><li><strong>奥特曼</strong><ul><li><strong>仅当</strong>超兽存在时才可变身为奥特曼。</li><li>变身后，<strong>必须专注于</strong>与超兽的作战。</li><li>直接与怪兽作战将会<strong>很难</strong>取胜</li><li>人间体形态下的活动，可能为奥特曼的战斗带来巨大的<strong>优势</strong>，也可能会是<strong>劣势</strong>。</li><li>奥特曼的活动同样会带来灾害。</li></ul></li></ul></li></ul><div class="hint-container tip"><p class="hint-container-title">提示</p><p>例如：收到改造的超兽，装备有着强力的武器，受亚波人控制，前往攻击城市，直接变身难以取胜</p><blockquote><p><strong>可采取的策略</strong>：</p><ol><li>配合游击兵团攻下附近的导弹基地，<strong>破坏怪兽身上的强力武器</strong></li><li>斩首控制超兽的亚波人，<strong>削弱怪兽战斗能力</strong></li><li>诱导超兽至<strong>远离城市</strong>的位置，<strong>变身</strong>应战</li></ol></blockquote></div><h2 id="怪兽少女" tabindex="-1"><a class="header-anchor" href="#怪兽少女" aria-hidden="true">#</a> 怪兽少女</h2><ul><li>神秘的半人半怪兽的少女，手拿耀石光剑，身手矫健，骁勇善战</li><li>与雷斯帕合作对敌，高效作战，合作连携技、切换作战</li><li>与雷斯帕分头行动，兼顾各处的战况 <ul><li>例如：雷斯帕突袭亚波人据点，怪兽少女保护游击兵阵地</li></ul></li></ul><h2 id="光明遗迹" tabindex="-1"><a class="header-anchor" href="#光明遗迹" aria-hidden="true">#</a> 光明遗迹</h2><ul><li>分布于世界各处的神秘遗迹</li><li>可以获得新的能力，运用能力完成谜题，可获得奖励和<strong>光晶碎片</strong></li><li><strong>光晶碎片</strong>，合成<strong>光晶</strong>后，可解锁奥特曼使用的新<strong>形态</strong>或<strong>技能</strong></li></ul><hr class="footnotes-sep"><section class="footnotes"><ol class="footnotes-list"><li id="footnote1" class="footnote-item"><p>类似于魔人化 <a href="#footnote-ref1" class="footnote-backref">↩︎</a></p></li></ol></section>',18);function y(F,I){const s=i("FontIcon"),r=i("RouterLink"),n=i("Badge");return g(),u("div",null,[_,p,c(" more "),t("details",f,[m,x,t("ul",null,[t("li",null,[l(r,{to:"/project_ultraman/story/"},{default:e(()=>[l(s,{icon:"read"}),b]),_:1})]),t("li",null,[l(r,{to:"/project_ultraman/design/"},{default:e(()=>[l(s,{icon:"creative"}),k]),_:1})]),t("li",null,[l(r,{to:"/project_ultraman/architecture/"},{default:e(()=>[l(s,{icon:"class"}),v]),_:1})])])]),B,j,N,t("ul",null,[V,q,C,t("li",null,[E,o("： "),t("ul",null,[t("li",null,[o("开放世界，自由探索宇宙人横行的大地 "),l(n,{text:"玩法"})]),t("li",null,[o("怪兽战场模式，既是人，也是奥，各取所长，成为英雄！"),l(n,{text:"玩法"})]),t("li",null,[o("可靠的伙伴，神秘的怪兽少女"),l(n,{text:"玩法"}),o(),l(n,{text:"故事"})]),t("li",null,[o("光宇遗迹，勇气与智慧的试炼 "),l(n,{text:"玩法"})]),t("li",null,[o("非对称多人模式，胜者是人类的团结，还是亚波人绝对的力量？"),l(n,{text:"玩法"})]),t("li",null,[o("不再局限于于人体结构的怪兽与外星人，感受它们的生命力与压迫感吧！"),l(n,{text:"美术"})]),t("li",null,[o("空想的浪漫，跌宕起伏的故事，出人意料地展开 "),l(n,{text:"故事"})]),t("li",null,[R,l(n,{text:"故事"})])])])]),w])}const S=a(h,[["render",y],["__file","index.html.vue"]]);export{S as default};
